mapper.select:
select device_id
       , name 
  from tb_user
 where device_id = #{deviceId, jdbcType=VARCHAR}
       and name = #{name, jdbcType=VARCHAR}

select count(1)
  from tb_user
 where device_id = #{deviceId, jdbcType=VARCHAR}
       and name = #{name, jdbcType=VARCHAR}

mapper.insert:
insert into tb_user(
    device_id, name
) values (
    #{deviceId}, #{name}
)
